#!/bin/sh
xrandr | grep -q "DP-0 connected" && xrandr --output DP-0 --primary --auto --output DP-1 --off --output HDMI-0 --off --output DP-2 --off --output DP-3 --off --output DP-4 --off || xrandr --output DP-2 --primary --auto --output DP-1 --off --output HDMI-0 --off --output DP-0 --off --output DP-3 --off --output DP-4 --off